#define PI 3.1415926535897932384626433832795


float aa_abs_radius = 0.5;
float field_rel_radius = 0.2;
float orbit_size = 0.5;
float orbit_time = 3.0;


float linearStep(float a, float b, float t)
{
    return (t - a) / (b - a);
}


float smoothStart3(float t)
{
    return t * t * t;
}


float smoothStop3(float t)
{
    float complementary = 1.0 - t;
    return 1.0 - complementary * complementary * complementary;
}


float smoothStep3(float t)
{
    return mix(smoothStart3(t), smoothStop3(t), t);
}


float field(vec2 center, float radius, vec2 point)
{
    return smoothStep3(
        clamp(
            linearStep(
                radius + field_rel_radius,
                radius - field_rel_radius,
                length(point - center)
            ),
            0.0,
            1.0
        )
    );
}


// this function also has the effect of bluring the transition
// which looks cool imo so i'm keeping it
// yes i'm aware the blurring depends on the resolution but shhhh
float threshold(float value)
{
    float smoothing_distance = aa_abs_radius / iResolution.y / field_rel_radius;
    return smoothstep(
        0.5 - smoothing_distance,
        0.5 + smoothing_distance,
        value
    );
}


void mainImage(out vec4 frag_color, in vec2 frag_coord)
{
    vec2 uv = frag_coord / iResolution.xy;
    vec2 scaled_coords = frag_coord / iResolution.y;
    vec2 scaled_res = iResolution.xy / iResolution.y;
    vec2 center = scaled_res * 0.5;

    float alpha = 0.0;
    alpha += field(center, 0.2, scaled_coords);

    float pos_x = sin(iTime * 2.0 * PI / orbit_time) * orbit_size;
    float pos_y = sin(iTime * 4.0 * PI / orbit_time) * orbit_size * 0.5;
    alpha += field(center + vec2(pos_x, pos_y), 0.125, scaled_coords);

    alpha = threshold(alpha);

    vec3 color = vec3(uv, 1.0);
    color = vec3(1.0) - (vec3(1.0) - color) * (1.0 - alpha);
    frag_color = vec4(color, 1.0);
}
