//take 2D point (x,y) and liniearaz it as a 1D float
//k is the precession for pixel precision it can be log2(resolution)+1
float to1D(float x, float y, int k)
{
    float z=0.;
    int n=2;
    float xx=0.,yy=0.;
    for(int i=0;i<k;i++){
        z*=2.;  if(x-xx>=1./float(n)) {z+=1.; xx+=1./float(n);}
        z*=2.;  if(y-yy>=1./float(n)) {z+=1.; yy+=1./float(n);}
        n*=2;
    }
    z = z / pow(2.,float(2*k));
    return z;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float z = to1D(uv.x,uv.y,10);

    float col = 0.; //sin(z*100.);//*pow(2.,iTime/10.);

    float r = .01;
    z +=iTime/1000.;
    col = smoothstep(r,r+.0001,fract(z*8.));


    // Output to screen
    fragColor = vec4(vec3(col),1.0);
}
