const vec2 offset_1 = vec2(0.35, 0.23);
const vec2 offset_2 = vec2(-0.25, -0.12);

const float scale_1 = 1.5;
const float scale_2 = 0.75;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 uv_1 = (uv - 0.5) * scale_1 + 0.5 + offset_1;
    vec2 uv_2 = (uv - 0.5) * scale_2 + 0.5 + offset_2;

    vec4 forground = texture(iChannel0, uv);
    vec4 forground_copy_1 = texture(iChannel0, uv_1);
    vec4 forground_copy_2 = texture(iChannel0, uv_2);
    vec4 background = texture(iChannel1, uv);

    if (forground.a > 0.0)
        forground_copy_2 = forground;
    if (forground_copy_1.a > 0.0)
        forground_copy_2 = forground_copy_1;

    fragColor = mix(background, forground_copy_2, forground_copy_2.a);
}

float th = 20.0; // threshold
float a2 = 1.2;

float getAlpha(vec4 c){
	return 1.0 - th*(c.g-a2*(max(c.r,c.b)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 fg = texture(iChannel0,uv);

    float alpha = step(0.5, getAlpha(fg));

	fragColor = vec4(fg.rgb, alpha);
}
