vec4 circleSmoothSetp(vec2 pos, vec2 center, float radius, vec4 color,float antialias) {

    float d = length(pos - center) - radius;
    float t = smoothstep(0., antialias, d);//(0.0,1.0)
    return vec4(color.rgb, 1.0 - t);
}

vec4 circleSetp(vec2 pos, vec2 center, float radius, vec4 color,float antialias) {

    float d = length(pos - center) - radius;
    float t = step( radius,d);
    return vec4(color.rgb, 1.0 - t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord;

    vec4 layer1 = vec4(1);

    vec4 layer2 = circleSmoothSetp(pos, 0.5 * iResolution.xy, 120., vec4(1.,.0,0,1.),8.);

    // Output to screen
    fragColor = mix(layer1, layer2, layer2.a);
}
