// Fabrice's refining - 2 

void mainImage( out vec4 C, vec2 U )
{
    U = 2. - 4.*U/iResolution.xy;
    vec2 P;
    P.y = length(U*U*U*U);
    P.x = asinh (U/P).y;
    C = sin( iTime + P.xyxx + vec4(0,4,8,1) );
}

