void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float pi = 3.141592653589793;
    float ar = iResolution.x / iResolution.y;

    vec2 uv = 2. * fragCoord / iResolution.xy - 1.;
    uv.r *= ar;

    vec2 pos = iMouse.z > 0. ?
        ( 2. * iMouse.xy / iResolution.xy - 1. ) * vec2( ar, 1.) :
        vec2( sin( iTime ), .125 + .25 * cos( 3. * iTime ) );

    float c  = atan( uv.x, uv.y );
    float p  = atan( pos.x, pos.y );
    float cp = atan( uv.x - pos.x, uv.y - pos.y );

    float v = sin( c  - p + pi * .5);
    float w = sin( cp - p - pi * .5);
    float u = sin( pi * .5 + c + cp - 2. * p );

    fragColor = vec4(.5-w*.25, .5-u*.25, .5-v*.25, 1.0);
}
