#define t iTime
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float z = t;
    vec2 p = fragCoord.xy/r;
    p -= .5;
    p.x *= r.x/r.y;
    p *= 10.;
	for(int i=0;i<3;i++) {
        vec2 a = p;
		z += .04;
        for(int j=0;j<5;j++) {
            a += vec2(sin(a.y+z), sin(a.x+z));
        }
		c[i] = .05/length(mod(a,1.)-.5);
	}
	fragColor = vec4(c, 1.);
}
