void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 c = texelFetch(iChannel1, ivec2(fragCoord), 0);

    float r = texelFetch(iChannel1, ivec2(fragCoord) - ivec2(1, 0), 0).r;
    if (r > 0.0)
    {
        c = vec4(0.5);
    }
    else if (r <= 0.0)
    {
        c = vec4(0.0);
    }

    float d = distance(iMouse.xy, fragCoord);
    if (iMouse.z > 0.0 && d <= 8.0)
    {
        c = vec4(1.0);
    }

    fragColor = c;
}

