void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Noise
    float noise = pow(texture(iChannel1,uv+sin(iTime*50.)).x,.1);

    //Ondulate screen up and down
    float oscSpeed = .2+abs(cos(iTime*.2)) + abs(sin(iTime*.12+.3)+.5);
    float amplitude = .6 * (pow(1.-abs(sin(iTime*.15)),6.) + oscSpeed*.01);
    uv.y+=sin(iTime*oscSpeed)*amplitude;

    //Greyscale
    vec3 color = texture(iChannel0,uv).xyz;
    color = vec3((color.x+color.y+color.z)/3.) * vec3(1,1.05,1) * noise;

    // Output to screen
    fragColor = vec4(color,1);
}
