const float power = 0.5;
const float speed = 8.0;
const float wave_count = 90.0;



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 forground = texture(iChannel0, uv);	


    vec2 pixelSize = vec2(1,1) / iResolution.x;
    vec2 fromMid = uv - vec2(0.5, 0.5);
    fromMid.y *= iResolution.y / iResolution.x;
    float dFromMid = length(fromMid);
    vec2 delta = fromMid * -power * (1.0 + sin(wave_count * dFromMid - speed * iTime));


    vec4 background = texture(iChannel1, uv+delta);


    fragColor = mix(background, forground, forground.a);
}

float th = 20.0; // threshold
float a2 = 1.2;

float getAlpha(vec4 c){
	return 1.0 - th*(c.g-a2*(max(c.r,c.b)));
}


