vec3 rgb2hsv(vec3 c) {
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


const float offset = 0.25;
const float speed = 1.5;



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 forground = texture(iChannel0, uv);
    vec4 forground_copy_1 = texture(iChannel0, uv + vec2(-offset, 0.0));
    vec4 forground_copy_2 = texture(iChannel0, uv + vec2(offset, 0.0));
    vec4 background = texture(iChannel1, uv);

    float norm_1 = length(forground_copy_1.rgb) / sqrt(3.0);
    float norm_2 = length(forground_copy_2.rgb) / sqrt(3.0);

    forground_copy_1.rgb = hsv2rgb(vec3(mod(norm_1 + sin(speed * iTime), 1.0), 1.0, 1.0));
    forground_copy_2.rgb = hsv2rgb(vec3(mod(norm_2 + sin(speed * iTime), 1.0), 1.0, 1.0));


    if (forground_copy_1.a > 0.0)
        forground_copy_2 = forground_copy_1;
    if (forground.a > 0.0)
        forground_copy_2 = forground;

    fragColor = mix(background, forground_copy_2, forground_copy_2.a);
}

float th = 20.0; // threshold
float a2 = 1.2;

float getAlpha(vec4 c){
	return 1.0 - th*(c.g-a2*(max(c.r,c.b)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 fg = texture(iChannel0,uv);

    float alpha = step(0.5, getAlpha(fg));

	fragColor = vec4(fg.rgb, alpha);
}
