#define A(u) texture(iChannel0,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = A(fragCoord);
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec2 v = 1.*(u        *2.-iResolution.xy)/iResolution.y;
    vec2 m = 1.*(iMouse.xy*2.-iResolution.xy)/iResolution.y;

    vec4 a = +A(u+vec2( 1, 0))
             +A(u+vec2( 0, 1))
             +A(u+vec2(-1, 0))
             +A(u+vec2( 0,-1))
             +A(u+vec2( 1, 1))
             +A(u+vec2(-1, 1))
             +A(u+vec2( 1,-1))
             +A(u+vec2(-1,-1))
             +A(u+vec2( 0, 0))

             +A(u+vec2( 2, 0))
             +A(u+vec2( 0, 2))
             +A(u+vec2(-2, 0))
             +A(u+vec2( 0,-2));
    uint s = uint(dot(a,vec4(1,1,0,0))+.1);
    vec4 o = A(u+vec2(0,0)).xxyz;
         o.x=float((+8U*4U*8U*4U* 16U //mod 7U to get similar world
                    +8U*4U*8U*    0U
                    +8U*4U*       0U
                    +8U*          3U
                    +             5U>>s)&1U);

    if(iFrame==0||iMouse.z>.5)
    {
        o = floor(fract(cos(dot(u,vec2(1.76543,iTime+1.5363)))*vec4(2467.5678,
                                                                    3467.5678,
                                                                    4467.5678,
                                                                    5467.5678))+.5);
        o*= step(dot(v,v),dot(m,m)*.1);
    }
    fragColor = o;
}

