const float step = -1.0 / 32.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 center_delta = uv * 2.0 - vec2(1.0);
    float center_distance = length(center_delta);
    vec2 dv = center_delta * pow(center_distance, 6.0);

    float m = 2.0 * iMouse.x / iResolution.x - 1.0;

    vec4 s0 = texture(iChannel0, uv + dv * step * 1.0 * m);
    vec4 s1 = texture(iChannel0, uv + dv * step * 2.0 * m);
    vec4 s2 = texture(iChannel0, uv + dv * step * 3.0 * m);
    vec4 s3 = texture(iChannel0, uv + dv * step * 4.0 * m);

    vec4 a =
        (
            s0 * vec4(1.00, 0.50, 0.75, 0.0) +
            s1 * vec4(0.75, 1.00, 0.50, 0.0) +
            s2 * vec4(0.50, 0.75, 1.00, 0.0) +
            vec4(0.0, 0.0, 0.0, 2.25)
        ) / 2.25;

    fragColor = a;
}
