vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

float gradientNoise(vec2 st){

    vec2 floor_st = floor(st);
    vec2 fract_st = fract(st);

    vec2 rand0 = random2(floor_st);
    vec2 rand1 = random2(floor_st+vec2(1., 0.));
    vec2 rand2 = random2(floor_st+vec2(1., 1.));
    vec2 rand3 = random2(floor_st+vec2(0., 1.));

    vec2 u = smoothstep(0.0, 1.0, fract_st);
    return mix (mix(dot(rand0, fract_st), 
                    dot(rand1, fract_st-vec2(1.,0.)), u.x),
                mix(dot(rand3, fract_st-vec2(0.,1.)), 
                    dot(rand2, fract_st-vec2(1.,1.)), u.x),
                u.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    mat2 convert = mat2(1.5, 1.2, -1.2, 1.5);
    st *= 5.;
    float column = floor(st.x);
    float noise;

    float noise1 = gradientNoise(st);
    st = convert*st;
    float noise2 = gradientNoise(st);
    st = convert*st;
    float noise3 = gradientNoise(st);
    st = convert*st;
    float noise4 = gradientNoise(st);

    if(column < 2.){
        noise = noise1;
    }else if(column < 4.){
        noise = (noise1+noise2)/2.;
    }else if(column < 6.){
        noise = (noise1+noise2+noise3)/3.;
    }else if(column < 8.){
        noise = (noise1+noise2+noise3+noise4)/4.;
    }

    vec3 col = vec3(.4+noise*.6);

    // Output to screen
    fragColor = vec4(col,1.0);
}
