#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(0.8),b,d)
#define S2(d,b) smoothstep(32.0*antialiasing(1.5),b,d)
#define PI     3.14159265
#define TWO_PI 6.28318530


vec3 DrawFigBorde(vec3 pColObj, vec3 colOut, float distObj )
{ colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj)-0.005,0.0));
  return colOut;
}


float sdBox( in vec2 p, in vec2 b )
{ vec2 d = abs(p)-b;  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);  }

float sdCircle( vec2 p, float r )
{ return length(p) - r;}


vec3 Base(vec2 p, vec3 colOut)
{   float d2=sdBox(p-vec2(10.0,-1.1), vec2(15.0,0.1) );
    colOut= DrawFigBorde(vec3(1.,0.0,0.0),colOut, d2 );
    return colOut;
}

vec3 Bola(vec2 p, vec2 pmv, float salto,  vec3 colOut, vec3 colIn)
{
   vec2 p0=p;
   float k=30.0;
   float t=mod(iTime,20.0);
   float x=t*5.0;
   float m=abs(salto*cos(x))*exp(-x/k);
   p.x-=t+pmv.x;
   p.y-=m+pmv.y;
   float d1= sdCircle( p,1.0);
   colOut= DrawFigBorde(colIn,colOut, d1 );   
   return colOut;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv2 = fragCoord.xy / iResolution.xy;
    uv2 *=  1.0 - uv2.yx;   //vec2(1.0)- uv.yx; -> 1.-u.yx; Thanks FabriceNeyret !
    float vig = uv2.x*uv2.y * 15.0; // multiply with sth for intensity
    vig = pow(vig, 0.35); // change pow for modifying the extend of the  vignette

    vec2 uv = -1.0 + 2.0 * fragCoord.xy/iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;
    uv-=vec2(0.0,-0.5);
    vec2 uv0=uv;
    float esc=8.0;
    uv*=esc;

    vec3 col=vec3(1.0);
         float tex = texture(iChannel0,uv/16.0).x;
    col = mix(col,vec3(0.5,0.4,0.4),tex)*0.85;
    col=col*vig;


    col= Base(uv-vec2(-10.0,1.0), col);
    col= Bola(uv-vec2(-9.0,1.0),vec2(-4.0,0.0),8.0 , col, vec3(1.0,0.2,0.2));
    col= Bola(uv-vec2(-9.0,1.0),vec2(-2.0,0.0),4.0, col, vec3(0.3));
    col= Bola(uv-vec2(-9.0,1.0),vec2(0.0,0.0),1.0, col, vec3(0.,0.0,1.0));


    col=pow(col,  vec3(0.554545));
    fragColor = vec4(col,1.0);
}

