vec2 noteFreq(Note note, float time)
{   float octava=3.0;
    float n1=sawFreq(time,note.frequency)* exp(-1.0/note.duration * (time - note.offset));  ///ORIGINAL
    float n1b=square(time*note.frequency)* exp(-1.0/note.duration * (time - note.offset));  ///ORIGINAL
    n1=(n1+n1b)/2.0;

    float n1a=n1+pow(n1, 2.0);
    float t=time;
    n1=Weight(n1)*n1+n1a;
    return vec2(n1*0.125);
}


