/* playlist: https://www.shadertoy.com/playlist/sXfSDS */

/*  
    Returns hash in range [0.0; 1.0].
*/
float sample1DHashUI32(uint value)
{
    // Pick some enthropy source values.
    // Try different values.
    const uint enthropy0 = 12345u;
    const uint enthropy1 = 67890u;

    // Calculate hash.
	value += enthropy1;
	value *= 445593459u;
	value ^= enthropy0;

    // 1.0f / 4294967295.0f = 2.32830644e-10

    return float(value * value * value) * 2.32830644e-10;
}

float valuetNoise1D(float u)
{
    // Fractial part.
    float fractU = u - floor(u);

    // Integer part.
    u = floor(u);

    // Smoothstep.
    float tU = fractU * fractU * (3.0 - 2.0 * fractU);

    // Pseudorandom samples.
    float sample0 = sample1DHashUI32(uint(u));
    float sample1 = sample1DHashUI32(uint(u + 1.0));

    // Linear interpolation.
    return sample0 * (1.0 - tU) + sample1 * (tU);
}

float fbm_noise(float x)
{
    float val = valuetNoise1D(x) * 1.0f;
          val += valuetNoise1D(x * 2.054f) * 0.65f;
          val += valuetNoise1D(x * 4.554f) * 0.225f;
          val += valuetNoise1D(x * 8.554f) * 0.125f;
          val += valuetNoise1D(x * 9.554f) * 0.325f;
          val += valuetNoise1D(x * 32.554f) * 0.125f;
    return val - 1.;
}

float dfdx(float x)
{
    const float dx = 0.01;
    return (fbm_noise(x+dx) - fbm_noise(x-dx)) / (2.*dx);
}

#define MOUSE_OFFSET ( iMouse.z > 0. ? iMouse.xy - iResolution.xy * 0.5 : vec2(0) )
vec2 map_to_centered_ndc(in vec2 SC, in float scale, in vec2 origin, in bool mouse_drag)
{
    vec2 M = MOUSE_OFFSET * (mouse_drag == true ? 1. : 0.);
    return ((2. * (SC - M) - iResolution.xy) / iResolution.y) * scale + origin;
}

float diskSDF_L2(in vec2 NDC, in vec2 C, in float r) { return length(C - NDC)-r; }

void mainImage( out vec4 O, in vec2 SC )
{
#define R                         ( iResolution )
#define UNIT                      (  4.0 / R.y  )
#define SMAA(x)                   ( smoothstep(UNIT, 0., x) )

    vec2 NDC = map_to_centered_ndc(SC, 1., vec2(0., 0.), true);

    vec3 color = vec3(1);

    float x = NDC.x + iTime * 0.5,
          fx = fbm_noise(x),
          deriv = dfdx(x),
          explicit_sdf_approx = NDC.y - fx,
          dsdx = sqrt(1.0 + deriv * deriv); // arclength change over argument change

          color = mix( color, vec3(0,0,0), SMAA(abs(explicit_sdf_approx) / dsdx) );
          color = mix( color, vec3(1,0,1), SMAA(diskSDF_L2(NDC, vec2(0., fbm_noise(iTime*0.5)), 2.*UNIT)) ); 

    O = vec4(color,1.0);
}
