#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float patchSize = 20.0;
    float speed = 1.0*iTime;
    float bumpynessR = sin(patchSize*uv.x)*sin(patchSize*uv.y);
    float bumpynessG = sin(PI+patchSize*uv.x)*sin(patchSize*uv.y);

    // Time varying pixel color
    vec3 col = vec3(1,0,0)*clamp(bumpynessR*abs(sin(speed)),0.0,1.0)+ vec3(1,0,0)*clamp(bumpynessG*abs(cos(speed)),0.0,1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
