vec2 movimento(vec2 p, float i) {
    float time = iTime;
    p.x += sin(time*i);
    p.y += cos((i*sin(time*.5)));
    return p;
}

vec3 expressao(vec2 p, float i) {
    vec3 color;

    p = movimento(p, i);

    float raio = sqrt(p.x*p.x + p.y*p.y);
    if (raio < .05+(i*.02)) {
        color = vec3(1,.5,.8);
    } else {
        color = vec3(0);
    }

    return color;
}
vec3 expressao(vec2 p) {
    vec3 color;

    float raio = sqrt(p.x*p.x + p.y*p.y);
    if (raio < .2*sin(iTime*2.5)+.3) {
        color = vec3(.5,.8,1.);
    }

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord*2.-iResolution.xy) / min(iResolution.x,iResolution.y);

    vec3 color;

    for (float  i = 1.; i <= 8.; i++) {
        color += expressao (uv, i);
    }

    color += expressao(uv);

    fragColor = vec4(color,1.0);
}
