float circle (vec2 uv, vec2 p, float r, float blur) {
    float d = length(uv - p);
    float c = smoothstep(r, r-blur, d);
    return c;   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float bodymask = 0.;
    float eyemask = 0.;
    float pupilmask = 0.;
    float mouthmask = 0.;
    bodymask = circle(uv, vec2(-0.25,0), 0.25, 0.1);
    bodymask += circle(uv, vec2(0.25,0), 0.25, 0.1);
    bodymask += circle(uv, vec2(0,-0.5), 0.75, 0.1);
    eyemask = circle(uv, vec2(-0.25,0), 0.25, 0.1);
    eyemask += circle(uv, vec2(0.25,0), 0.25, 0.1);
    pupilmask = circle(uv, vec2(-0.25,0), 0.24, 0.1);
    pupilmask += circle(uv, vec2(0.25,0), 0.24, 0.1);

    mouthmask = bodymask* circle(uv, vec2(0,-.9), 0.8, 0.1);
    //col = vec3(0,0,1);
    if (bodymask >= 0.85) {
        col = vec3(0,.9,0);
    }
    if (bodymask >= 0.9) {
        col = vec3(0,1,0);
    }
    if (eyemask >= 1.0) {
        col = vec3(1,1,1);
    }
    if (pupilmask >= 1.0) {
        col = vec3(0,0,0);
    }
    if (mouthmask >= 1.0) {
        col = vec3(0.6,1,0);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
