precision mediump float;

float plot(vec2 uv, float pct){
  return smoothstep(0.02, 0.0, abs(uv.y - pct));
}

vec3 getcolor(vec2 uv, float y){
    vec3 green = vec3(0,1,0);
    vec3 black = vec3(0,0,0);
    float f = plot(uv, y);
    return mix(black, green, f);
}

vec3 step1(vec2 uv){
    float y = sqrt(pow(0.4, 2.0) - pow(uv.x - 0.5, 2.0)) + 0.5;
    return getcolor(uv, y);
}

vec3 step2(vec2 uv){
    float y = sqrt(pow(0.4, 2.0) - pow(uv.x - 0.5, 2.0)) + 0.5;
    return getcolor(uv, 1.0-y);
}

vec3 step3(vec2 uv){
    float y = sqrt(pow(0.1, 2.0)-pow((abs(uv.x - 0.5) - 0.2), 2.0))/2.0 + 0.7;
    return getcolor(uv, y);
}

vec3 step4(vec2 uv){
    float y = - sqrt(pow(0.1, 2.0)-pow((abs(uv.x - 0.5) - 0.2), 2.0)) + 0.65;
    return getcolor(uv, y);
}


vec3 step5(vec2 uv){
    float y = sqrt(0.002-pow((abs(uv.x - 0.5 + sin(4.0*iTime)/30.0)-0.2), 2.0))*2.0/3.0 + 0.65;
    return getcolor(uv, y);
}

vec3 step6(vec2 uv){
    float y = 0.35-sqrt(pow(0.3, 2.0)-pow(uv.x - 0.5, 2.0))*(1.0/4.0+pow(0.5+0.5*sin(2.0*3.1415926*iTime),6.0)/10.0);
    return getcolor(uv, y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord/iResolution.xy;
    uv = vec2(smoothstep(0.2, 0.7625, uv.x), smoothstep(0.0, 1.0, uv.y));
    vec3 color =  step1(uv) + step2(uv) + step3(uv) + step4(uv) +step5(uv) + step6(uv);
	fragColor = vec4(color.rgb, 1);

}
