float circle (vec2 uv, vec2 p, float r, float blur) {
    float d = length(uv - p);
    float c = smoothstep(r, r-blur, d);
    return c;   
}

vec3 drawFrog(vec2 uv) {
    vec3 col = vec3(0,0,0);
    float bodymask = 0.;
    float eyemask = 0.;
    float pupilmask = 0.;
    float mouthmask = 0.;
    bodymask = circle(uv, vec2(-0.25,0), 0.25, 0.2);
    bodymask += circle(uv, vec2(0.25,0), 0.25, 0.2);
    bodymask += circle(uv, vec2(0,-0.5), 0.75, 0.2);
    eyemask = circle(uv, vec2(-0.25,0), 0.25, 0.1);
    eyemask += circle(uv, vec2(0.25,0), 0.25, 0.1);
    pupilmask = circle(uv, vec2(-0.25,0), 0.24, 0.1);
    pupilmask += circle(uv, vec2(0.25,0), 0.24, 0.1);

    mouthmask = bodymask* circle(uv, vec2(0,-.9), 0.8, 0.1);
    //col = vec3(0,0,1);
    if (bodymask >= 0.9) {
        col = vec3(0,1,0);
    }
    if (eyemask >= 1.0) {
        col = vec3(1,1,1);
    }
    if (pupilmask >= 1.0) {
        col = vec3(0,0,0);
    }
    if (mouthmask >= 1.0) {
        col = vec3(0.6,1,0);
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    // Time varying pixel color
    vec3 ogcol = 0.5 + cos(iTime*.5+uv.xyx+vec3(0,2,4))*vec3(1,0.2,1);
    vec3 col = vec3(0,0,0);
    for (float j=1.; j <= 10.; j++) {
        for (float i = 1.; i <= 10.; i ++) {
            vec2 smoluv = uv*5.0;
            smoluv.x += uv.x - 10. + i*2.+mod(iTime,2.);
            smoluv.y += uv.y + j*2. - 10.;
            vec2 smoluv2 = uv*5.0;
            smoluv2.y += uv.y - 10. + i*2.+mod(iTime,2.);
            smoluv2.x += uv.x + j*2. - 11.;
            //smoluv.x = smoluv.x*i;
            col += drawFrog(smoluv);
            col += drawFrog(smoluv2);
        }
    }

    col *= ogcol;
    col += ogcol*.2;
    // Output to screen
    fragColor = vec4(col,1.0);
}
