void mainImage(out vec4 o, vec2 u)
{ 
    ivec4 b = ivec4(o -= o);
    float t = .1*iTime;

    for (vec2 R = iResolution.xy; (b.x^b.y^b.z)%99 > b.z-8 ; )
        b = ivec4((2. + cos(t))*(2.*(u-=2.*sin(t))-R)/R.y
                * mat2( cos( vec4(0,33,55,0) - .03*o.a-.3*t))
                * o.a
                + 2e2 + vec2(25,9)*t
                , o+=.1 );

    o = o.a < 8.1 ? .1+sin(t/.1)*vec4(b%32 & b.x%9 & b.z%9) : o/65. - .02*vec4(b%3);
}

