//抗锯齿
vec4 circle(vec2 pos, vec2 center, float radius, vec3 color, float antialias) {
    float d = length(pos - center) - radius;
    float t = smoothstep(0.0, antialias, d);
    return vec4(color, 1.0 - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec4 parameters = vec4(0.5, 0.5, 100, 1) ;// Center: (x, y), Radius: z
	vec4 circleColor  = vec4(0.5, 0.5, 1, 1);
	vec4 backgroundColor  = vec4(1, 1, 1, 1);


    vec2 pos = fragCoord; // pos.x ~ (0, iResolution.x), pos.y ~ (0, iResolution.y)

    vec4 layer1 = vec4(backgroundColor.rgb, 1.0);
    vec4 layer2 = circle(pos, parameters.xy * iResolution.xy, parameters.z, circleColor.rgb, parameters.w);

    fragColor = mix(layer1, layer2, layer2.a);

}
