bool inside(vec2 pos)
{
  vec3 greenscreen = vec3(0.05, 0.64, 0.15);
  vec3 col = texture(iChannel0, pos).rgb;
  return length(col - greenscreen) > 0.1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 right = vec2(1.0, 0.0);
    vec2 down = vec2(0.0, 1.0);

    vec3 x = vec3(0.0);
    if (!inside(uv))
    {
        if (inside((fragCoord + right) / iResolution.xy) ||
            inside((fragCoord + down) / iResolution.xy) ||
            inside((fragCoord - right) / iResolution.xy) ||
            inside((fragCoord - down) / iResolution.xy))
            x = vec3(1.0);
        else
            x = vec3(0.0, 0.58, 0.88);
    }
    // Output to screen
    fragColor = vec4(x,1.0);
}
