//golfed version by FabriceNeyret2
void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
         U = ( R/20. -.5*U ) / R.y;

    float s = .5, t = 1./1.27,
          S = 1./t + mod( iTime/16.*t, 2.);
    O-=O;          
    for( int N=12, i, c=0; 
         c++ < N; 
         O = ( vec4(O.rg,abs(U.y-U)) + O.gbra ) / 2.125
       )        
        for( i=0; i++ < N; )       
            U = -fract( ( U.yx + U*mat2(s,s,t,t ) * s  ) * t * vec2(-S,1./S) );       

    O *= 3.;    
}

