float circle(vec2 st, float r) {
 return smoothstep(r, r+0.02, distance(st, vec2(0.5)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.);
    uv*=12.;

    if(mod(floor(iTime),2.)< 1.0) {
       float flag = step(1., mod(uv.y,2.0)) < 1.0 ? -1. : 1.;
       uv.x += flag*5.*iTime*0.4;
    } else {
      float flag = step(1., mod(uv.x,2.0)) < 1.0 ? -1. : 1.;
      uv.y += flag*5.*iTime*0.4;
    }


    uv = fract(uv);

    col = vec3(circle(uv, 0.3));

    fragColor = vec4(col,1.0);
}
