void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float px = 1./iResolution.y;
    vec4 n = texture(iChannel2,uv+iTime*vec2(0.0,px));
    vec4 oc = texture(iChannel0,uv+vec2((n.r-.5)*0.001,px));
    vec4 c = texture(iChannel1,uv+vec2(0.,iTime*n.b*.01+n.rg*.1+.08));
    fragColor = mix(c,oc,step(px,uv.y));
}
