void mainImage( out vec4 O,  vec2 U )
{
    vec2 R = iResolution.xy, L, B, P;
    U = 1e2*( U+U - R ) / R.y;

    B = mod(iTime/vec2(1,2),2.); B = .5-min(B,2.-B); // ball trajectory
    P = vec2(80, 70.*B.y);                           // pad trajectory

#define A  min(L=abs(P-U//
#define M  ,L.y ).x > 1. ? O : O//
//     reading hint: A[...]M
#define D  ; P = -P; A +R) M --; \
                     A -R) M --; \
                max(A )/(R-1.),L) M = C

    vec4 C = vec4(9,0,0,1);
    O -= O-1.;                              // background ( O = 1.-O bugs on old Macs )
    R = vec2(16,46)
    D                                       // left pad
    D.zzxw;                                 // right pad
    R.y = 13.; P = 1e2*B
    D.xxzw;                                 // ball
}





