float egg(vec2 uv)
{
   vec2 a = vec2(.8,.5);
    float x = a.x-uv.x;
    float y = a.y-uv.y;
    float p = 2.5;
    return pow(abs(x)/5.,p) + pow(abs(y)/6.,p);

}

vec2 zhoukowski(vec2 uv)
{
    vec2 w =(uv+1./uv);
    return w;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
uv.x = uv.x*1.7;
    uv.x = uv.x-0.6;
    uv.y = uv.y-0.2+sin(iTime)*0.01;
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    uv = zhoukowski((uv)*3.+0.4);
    if( egg(uv) < 0.1)
    {
        vec2 a = vec2(.8,.5);
        float t = smoothstep(0.9,.0,length(a-uv*0.376));
        fragColor = vec4(pow(t,0.16));
    }
    else{
    // Output to screen
    fragColor = vec4(vec3(1.),1.0);
    }
}
