#define RAY_WIDTH 2.0
#define BLOOM_RADIUS 180.0
#define RAY_COL vec3(0.4, 1.0, 1.0)

#define LEFT_NUM 6
#define Right_NUM 6

#define FRQ 5.0

float distanceLine(vec2 uv, float r, float o) {
    return abs(uv.x + r * uv.y - o - 0.5) / sqrt(2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uvs = fragCoord/iResolution.xy;

    float rayw = RAY_WIDTH / iResolution.x;
    float bloomr = BLOOM_RADIUS / iResolution.x;

    vec3 col = vec3(.0);

    float st = 0.05;

    float baseRL = .55;

    float baseRR = -.55;

    for (int i = 0; i < LEFT_NUM; i++) {
        col = mix(RAY_COL, col, clamp(.0, 1.0, pow(distanceLine(uvs, baseRL, 0.3 - float(i) * st)/bloomr, .1)));
    }

    for (int i = 0; i < Right_NUM; i++) {
        col = mix(RAY_COL, col, clamp(.0, 1.0, pow(distanceLine(uvs, baseRR, -0.3 + float(i) * st)/bloomr, .1)));
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}
