// Texturing
// Eric Galin

const float View = 2800.0;  // View distance
const float Epsilon = 0.01; // Marching epsilon
const int Steps = 400;      // Number of steps

// Lipschitz constant
const float K=1.0;


// Structure for objects
// v : Field value
// i : Texture index
struct V {
    float v;
    int i;
};

// Structure for texture
// c : Color
// s : Specular
struct T {
vec3 c;
float s;
};


// Terrain -----------------------------------------------------------------------------------------------

//fbm

float fbm(vec2 p,float l, float a ,int o){
    float t=0.0;
    for(int i=0;i<o;i++){
        t+=a*Noise(p/l);
        a*=0.5;
        l*=0.5;
    }
   return t;
}

//ridge
float ridge(vec2 p){
   float n=Noise(p);
   return 1.0-2.0*abs(n);
}

//hetefbmridge
float heteFbmRidge (vec2 p, float l,float a, int o,float amin, float amax){
    float t= a*ridge(p/l);
    for (int i=0;i<o;i++){
        a*=0.5;
        l*=0.5;
        float att = smoothstep(amin,amax,t);
        t+=att*a*ridge(p/l);
    }
   return t;
}

//plateau

float plateau(float h,float e,float a){
    if( (h>e) || (h>e+a) ){
        return h;
    }
    float u=(h-e)/(e+a)-e;
    float c=u*u;
    h=e-a*c;
    return h;
}
//Terrain ---------------------------------------------------------------------------

// Terrain heightfield
// x : Position in the plane
float Terrain( in vec2 p )
{
    return heteFbmRidge(p,80.0,50.0,4,2.0,40.0);
}

// Implicit surface defining the terrain
// p : Point
V ImplicitTerrain(in vec3 p, int index)
{
    float z = Terrain( p.xy );
    float h = (p.z - z)/8.0;
    return V (h, index);
}

//water 

float Water(in vec2 x)
{
    return 20.0*Noise(x/50.0+iTime)- 20.0;
}

V ImplicitWater(in vec3 p, int index)
{
    float h = p.z - Water( p.xy );
    return V (h, index);
}
// Primitive functions -----------------------------------------------------------------------------------

// Sphere
// p : Point
// c : Center
// r : Radius
float Sphere(vec3 p, vec3 c,float r)
{
  return length(p-c)-r;
}

// Intersection, preserve the index of first object
// a, b : field function of left and right sub-trees
float Intersection(float a,float b)
{
    return max(a,b);
}

// Intersection, preserve the index of first object
// a, b : field function of left and right sub-trees
V Intersection(V a,V b)
{
    if (a.v>b.v)
    {
        return V(a.v,a.i);
    }
    else
    {
        return V(b.v,a.i);
    }
}

// Union
float Union(float a,float b)
{
    return min(a,b);
}

// Union
// a : field function of left sub-tree
// b : field function of right sub-tree
V Union(V a,V b)
{
    if (a.v<b.v)
        return V(a.v,a.i);
    else
    {
        return V(b.v,b.i);
    }
}

// DiffÃ©rence
float Diff(float a , float b){
    return max(a,-b);
}

// Difference
V Diff ( V a, V b ) {
    return V(max(a.v, -b.v), a.i);
}


// Plane
// p : point
// n : Normal of plane
// o : Point on plane
float Plane(vec3 p, vec3 n, vec3 o)
{
    return dot((p-o),n);
}

// p : point
// n : Normal of plane
// o : Point on plane
// index : Texture index
V Plane(vec3 p, vec3 n, vec3 o, int i){
    return V( dot((p-o), n), i );
}

//capsule
float capsule(vec3 a, vec3 b, float r, vec3 p){
    if(dot(p-a,(b-a)/length(b-a))<0.0){
      return length(p-a)-r;
    }
    else if(dot(p-b,(b-a)/length(b-a))>0.0){
      return length(p-b)-r;
    }else {
    float aq=dot(p-a,(b-a)/length(b-a));
    float l=sqrt(dot(p-a,p-a)-aq*aq);
    return l-r;
    }
}

//capsule struct
V capsule(vec3 a, vec3 b, float r, vec3 p,int i){
    if(dot(p-a,(b-a)/length(b-a))<0.0){
      return V(length(p-a)-r,i);
    }
    else if(dot(p-b,(b-a)/length(b-a))>0.0){
      return V(length(p-b)-r,i);
    }else {
    float aq=dot(p-a,(b-a)/length(b-a));
    float l=sqrt(dot(p-a,p-a)-aq*aq);
    return V(l-r,i);
    }
}

//cylindre
float cylindre(vec3 p, vec3 a , vec3 b , float r){
    float dc=capsule (a,b,r,p);
    float f= Intersection ( dc, Plane ( p,(a-b)/length(b-a),a+vec3(0.0,0.0,0.0)) );
    float g= Intersection ( dc,Plane(p,(b-a)/length(b-a),b+vec3(0,0,0)) );
    return Intersection(f,g);
}

//cylindre with struct
V cylindre(vec3 p, vec3 a , vec3 b , float r,int i){
    V dc=capsule (a,b,r,p,i);
    V f= Intersection ( dc, Plane ( p,(a-b)/length(b-a),a+vec3(0.0,0.0,0.0),i) );
    V g= Intersection ( dc,Plane(p,(b-a)/length(b-a),b+vec3(0,0,0),i) );
    return Intersection(f,g);
}

vec3 Bois(vec3 p,vec3 a ,vec3 b){
        p = p +0.1*Noise(p/0.1);
        float d=length(p.xy);
        float v=0.5*(cos(d*10.0)+1.0);
        vec3 c = a*v +b*(1.0-v);
        //vec3 c =mix(a,b,v);
        return c;
}

int convert(float f ){
    if( f<0.0 ){
        f=f-1.0;
    }
    return int(f);
}

vec3 Damier(vec3 p, vec3 a, vec3 b){
    int x=convert(p.x);
    int y=convert(p.y);
    int z=convert(p.z);
    if ((x+y+z) %2 == 0){
        return a;
    }else {
        return b;
    }
}

vec3 Sphere(vec3 p ,vec3 a , vec3 b){
    vec3 q = fract(p);
    if(length(q-vec3(0.5,0.5,0.5))<0.6){
        return a;
    }else {
        return b;
    }
}

//tore
float tore(vec3 p , vec2 r ){
    float x =length(p.xz)-r.x;
    return length(vec2(x,p.y))-r.y;
}

//tore with struct
V tore(vec3 p , vec2 r,int i ){
    float x =length(p.xz)-r.x;
    return V(length(vec2(x,p.y))-r.y,i);
}


//box
float box (vec3 p, vec3 o, float r) {
    return Intersection (
        Plane (p,vec3(1.0,0.0,0.0),o+vec3(r,0.0,0.0)),
        Intersection (
            Plane (p,vec3(-1.0,0.0,0.0),o+vec3(-r,0.0,0.0)),
            Intersection (
                Plane (p,vec3(0.0,1.0,0.0),o+vec3(0.0,r,0.0)),
                Intersection (
                    Plane (p,vec3(0.0,-1.0,0.0),o+vec3(0.0,-r,0.0)),
                    Intersection (
                        Plane (p,vec3(0.0,0.0,1.0),o+vec3(0.0,0.0,r)),
                        Plane (p,vec3(0.0,0.0,-1.0),o+vec3(0.0,0.0,-r)))))));
}
// box with struct
    V box (vec3 p , vec3 o,float r,int i){
        return V(Intersection (
        Plane (p,vec3(1.0,0.0,0.0),o+vec3(r,0.0,0.0)),
        Intersection (
            Plane (p,vec3(-1.0,0.0,0.0),o+vec3(-r,0.0,0.0)),
            Intersection (
                Plane (p,vec3(0.0,1.0,0.0),o+vec3(0.0,r,0.0)),
                Intersection (
                    Plane (p,vec3(0.0,-1.0,0.0),o+vec3(0.0,-r,0.0)),
                    Intersection (
                        Plane (p,vec3(0.0,0.0,1.0),o+vec3(0.0,0.0,r)),
                        Plane (p,vec3(0.0,0.0,-1.0),o+vec3(0.0,0.0,-r))))))),i);}

//Forme Temple
V Temple (vec3 p, int i) {
    float t;    
    return V (Union(t,cylindre(p,vec3(-5.0,-5.0,0.0),vec3(-5.0,-5.0,10.0),1.0) ),i);

}

// Sphere 
// p : Point
// c : Center 
// r : Radius
// index : Texture index
V Sphere(vec3 p, vec3 c, float r, int index) {
  return V(length(p-c)-r,index);
}

// Scene --------------------------------------------------------------------------

// Potential field of the object
// p : point
V Object(vec3 p)
{
      V u =ImplicitTerrain(p,2);
      u=Union(u,ImplicitWater(p,2));
      u=Union(u,box(p,vec3(300.0,50.0,200.0),50.0,8));
      u=Union(u,tore(p,vec2(200.0,20.0),2));
      u=Union(u,tore(p+vec3(100.0,0.0,0.0),vec2(200.0,20.0),2));
      u=Union(u,tore(p+vec3(200.0,0.0,0.0),vec2(200.0,20.0),2));
      u=Union(u,Sphere(p-vec3(0.0,0.0,200),vec3(10.0,0.0,0.0),40.0,6));
      u=Union(u,Sphere(p-vec3(-210.0,0.0,200),vec3(10.0,0.0,0.0),40.0,6));
      u=Union(u,Sphere(p-vec3(-100.0,0.0,200 ) , vec3 (10.0,0.0,0.0),40.0,10));


     return u;
}


// Analysis of the scalar field --------------------------------------------------------------------------

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.001;
  vec3 n;
    float v = Object(p).v;
  n.x = Object( vec3(p.x+eps, p.y, p.z) ).v - v;
  n.y = Object( vec3(p.x, p.y+eps, p.z) ).v - v;
  n.z = Object( vec3(p.x, p.y, p.z+eps) ).v - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, float e,out bool h,out int s)
{
  h = false;

    // Start at the origin
    float t=0.0;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = Object(p).v;
    // Hit object
      if (v < 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,v/K);
      // Escape marched too far away
      if (t>e)
      {
          break;
      }
  }
  return t;
}


// Shading and lighting ---------------------------------------------------------------------------

vec3 TextureRock(in vec3 p)
{
  return mix(vec3(0.2,0.0,0.0),vec3(0.74,0.72,0.72),Fbm(p/50.0));   
}

vec3 TextureWater(in vec3 p)
{
  return vec3(0.52,0.75,0.91);   
}

vec3 TextureBorder(in vec3 p)
{
    return vec3(0.25,0.24,0.24);   
}

vec3 TexturePlage(in vec3 p)
{
      return vec3(1.0,0.85,0.45);
}


vec3 TextureSnow(in vec3 p)
{
    return vec3(1.0,1.0,1.0);
}

vec3 TextureTemple (in vec3 p)
{
    return vec3(1.0,1.0,0.0);
}

vec3 TextureBois(in vec3 p)
{
  return Bois(p,vec3(0.533,0.2588,0.11),vec3(0.65,0.4,0.15));
}

vec3 TextureDamier(in vec3 p)
{
  return Damier(p,vec3(0,0,0),vec3(1,1,1));
}

// Compute color
// p : Point
// n : Normal
vec3 Color(in vec3 p,in vec3 n)
{
    V vp = Object (p);
    if (p.z <= 0.0 && vp.i == 2) {
        return TextureWater(p);
    }

    if (p.z >= 0.0 && p.z <= 250.0 && vp.i == 4) {
         return TextureTemple(p);
    }

    vec3 c = TextureWater(p);

    if (p.z > 0.0 && p.z<= 15.0 && vp.i == 2) {
        float beach = smoothstep(0.0, 10.0, p.z + 2.0*Noise(p/2.0));
        c = mix(c, TexturePlage(p/10.0), beach);
        return c;
    }
    vec3 c1 = TexturePlage(p);

    if (p.z > 15.0 && p.z <= 150.0 && vp.i ==2) {
        float s = smoothstep(10.0, 40.0, p.z + 2.0*Noise(p/2.0));
        c1 = mix(c1, TextureSnow(p/10.0), s);
        return c1;
    }

    vec3 c2 = TextureSnow(p);

     if (p.z > 100.0 && p.z <= 350.0 && vp.i == 2) {
        float snow = smoothstep(200.0, 145.0, p.z + 2.0*Noise(p/2.0));
        c2 = mix(TextureSnow(p/10.0), c2, snow);
        return c2;
        }
    vec3 c3= TextureBois(p);
        if(vp.i==8){
        float b =smoothstep(100.0, 145.0, p.z + 1.0*Noise(p/2.0));
        c3 = mix(TextureBois(p/100.0), c3, b);
        return c3;        
    } 

    vec3 c4 = TextureDamier(p);
    if(vp.i==10){
        return c4;  
    }


}



// Rendering
// ro, rd : Ray origin and direction
// pip : Picture in picture boolean
vec4 Render( in vec3 ro, in vec3 rd, bool pip )
{
    // Light direction
    vec3 light1 = normalize( vec3(-0.8,-0.3,0.4) );


    vec3 col;
    int it;
    bool b;
    float t = SphereTrace( ro, rd, View,b , it);
    vec3 p = ro + t*rd;
    V vp= Object(p);
    vec3 n = ObjectNormal( p );


    col = Color(p,n);
    // Sky
    if( b==false )
    {
        col = vec3(0.35,0.65,0.95) - rd.z*rd.z*2.5;
    }
    else
    {
        vec3 ref = reflect( rd, n );
        float fre = clamp( 1.0+dot(rd,n), 0.0, 1.0 );
        vec3 hal = normalize(light1-rd);


         // Pseudo diffuse lighting
        float dif = 0.5*(1.0+dot( light1, n ));
        dif*=dif;

        col = 0.45*col+0.55*dif*vec3(0.35,0.35,0.35);
    }

    // Shading for iterations
    if (pip==true)
    {
        float s=float(it)/float(Steps);
        return vec4(0.112+0.824*s,0.418+0.556*s,0.611-0.515*s,t);
    }
    else
    {
        // Gamma with square root
        return vec4( sqrt(col), t );
    }
}

vec2 RayDirection(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/4.0;
    // Picture in picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p=(-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip=true;
    }
       else
       {
           pip=false;
       }
   }
   return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   // Time as function of the position of the mouse
    float a = 3.14*2.0*1.5*iMouse.x/iResolution.x;

    // Camera transform
    vec3 ro;
    mat3 cam = Camera( a, ro);   

    // Picture in picture
    bool pip=true;

    // Pixel coordinates
    vec2 p = RayDirection(fragCoord, pip);

    // Camera ray    
    vec3 rd = cam * normalize(vec3(p,5.0));

    // Render
    vec4 res = Render( ro, rd, pip );

    fragColor = vec4( res.xyz, 0.0 );

}

// Texturing
// Eric Galin

// Texturing and noise ---------------------------------------------------------

// Hashing function
// Returns a random number in [-1,1]
// p : Vector in space
float Hash(in vec3 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

// Procedural value noise with cubic interpolation
// x : Point 
float Noise(in vec3 p)
{
    vec3 i = floor(p);
    vec3 f = fract(p);

    f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    // f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix(mix( Hash(i+vec3(0,0,0)), 
                        Hash(i+vec3(1,0,0)),f.x),
                   mix( Hash(i+vec3(0,1,0)), 
                        Hash(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( Hash(i+vec3(0,0,1)), 
                        Hash(i+vec3(1,0,1)),f.x),
                   mix( Hash(i+vec3(0,1,1)), 
                        Hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}

// Hashing function
// Returns a random number in [-1,1]
// p : Vector in the plane
float Hash(in vec2 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*(p.x+p.y) );
}


// Procedural value noise with cubic interpolation
// x : Point 
float Noise(in vec2 p)
{
    vec2 i = floor(p);
    vec2 f = fract(p);

    f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    // f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix( Hash(i+vec2(0,0)), 
                        Hash(i+vec2(1,0)),f.x),
                   mix( Hash(i+vec2(0,1)), 
                        Hash(i+vec2(1,1)),f.x),f.y);
}

// Fractal brownian motion with 4 frequencies
// p : Point
float Fbm(in vec3 p)
{
    return Noise(p)/2.0+Noise(p*2.0)/4.0+Noise(p*4.0)/8.0+Noise(p*8.0)/16.0;
}

// Camera
// a: Rotation angle around z axis
// ro : Ray origin
mat3 Camera(float a, out vec3 ro)
{
	float range=1350.0;
    // Origin
    ro = vec3(range*cos(a),range*sin(a),range/4.0);

    // Target
    vec3 ta = vec3(0.0,0.0,50.0);

	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(0.0, 0.0,1.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}
