#define PI 3.14159265358979323
#define T .08
#define ZOOM 100.

vec2 cexp(vec2 z, vec2 c)
{
    float argz, m, a;
    float lnz = dot(z,z);

    if (lnz == 0.) {
        return vec2(0., 0.);
    } else {
        lnz = 0.5*log(lnz);
        argz = atan(z.y, z.x);

        m = exp(c.x*lnz - c.y*argz);
        a = c.x*argz + c.y*lnz;

        return m*vec2(cos(a), sin(a));
    }
}

vec2 clog(vec2 z)
{
    vec2 w;

    w.x = 0.5*log(dot(z,z));
    w.y = atan(z.y, z.x);

    return w;
}

vec2 cmul(vec2 z1, vec2 z2)
{
    return mat2(z1, -z1.y, z1.x)*z2;
}

vec2 cdiv(vec2 z1, vec2 z2) //z1/z2
{
    return z1 * mat2(z2, -z2.y, z2.x) / dot(z2, z2);
}

vec3 hrgb(vec2 w)
{
    vec3 c;
    float mm;
    float p;

    float m = length(w);
    float a = (PI + atan(w.y, w.x))/(2.0*PI);

    c.r =  0.5 - 0.5*sin(2.0*PI*a - PI/2.0);
    c.g = (0.5 + 0.5*sin(2.0*PI*a*1.5 - PI/2.0)) * float(a < 0.66);
    c.b = (0.5 + 0.5*sin(2.0*PI*a*1.5 + PI/2.0)) * float(a > 0.33);

    mm = fract(m);
    p = fract(mod(a, 1.0/16.0));

    c -= 0.15*mm + 1.5*p;

    if (/*length(w) + T/2. > 1. &&*/
        (fract(w.x) < T || fract(-w.x) < T ||
         fract(w.y) < T || fract(-w.y) < T)) c -= 0.4;

    return c;
}

vec2 f(vec2 z)
{
    return cexp( vec2(exp(1.), 0.), z );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 z, w, w1, w2, w3, w4;

    z = fragCoord - iResolution.xy/2.;


    if (iMouse.xy != vec2(0., 0.)) //to center at origin
        z -= iMouse.xy - iResolution.xy/2.;


    //z /= ZOOM;

    w  = f(z/ZOOM);
    w1 = f((z + vec2(-.25,-.25))/ZOOM );
    w2 = f((z + vec2(-.25, .25))/ZOOM );
    w3 = f((z + vec2( .25,-.25))/ZOOM );
    w4 = f((z + vec2( .25, .25))/ZOOM );


    fragColor = vec4(0.2*(hrgb(w) +
                          hrgb(w1) + hrgb(w2) + 
                          hrgb(w3) + hrgb(w4)), 1.0);
}
