#define PI 3.14159265359
#define TWO_PI 6.28318530718

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float drawNshape(int N, vec2 uv, float size, vec2 offset, float angle){



      vec2 st = uv*2.0-(offset);
      float a = atan(st.x,st.y)+angle;
      float r = TWO_PI/float(N);
      float d = 0.0;
      d = cos(floor(.5+a/r)*r-a)*length(st);
      return 1.0-smoothstep(size+(0.001*size),size-(0.001*size),d);
}


float drawSquare(vec2 uv, float size, vec2 offset){
uv -= offset;
    vec2 bottomRight = step (vec2(size, size), uv);
    vec2 topLeft = step (vec2(size, size), 1.0 - uv);
    return bottomRight.x*bottomRight.y*topLeft.x*topLeft.y;
}

float drawCircle (vec2 uv, float _radius){

    vec2 dist = uv - vec2(0.5);
    return smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(dist,dist)*4.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x -= 0.250;
    uv.x *= iResolution.x/iResolution.y;
    vec4 tex0 = texture(iChannel0, uv);
    vec2 translate = vec2(0.5*cos(iTime), 0.7*sin(iTime));
    uv += translate*0.15;


    vec3 color = vec3(0.0);
    vec3 t_color = vec3(0.0);

    color += (1.0 - drawCircle(uv, 0.453+0.01*abs(sin(iTime))))*drawCircle(uv, 0.451-0.01*abs(sin(iTime)));
    color *= 1.0-vec3(step (0.4905, uv.x)*step (0.4905, 1.0-uv.x));
    color *= 1.0-vec3(step (0.4905, uv.y)*step (0.4905, 1.0-uv.y));

    color += 0.35*(1.0 - drawCircle(uv, 0.00203)); 
    color *= 1.0-vec3(step (0.4985, uv.x)*step (0.4985, 1.0-uv.x));
    color *= 1.0-vec3(step (0.4985, uv.y)*step (0.4985, 1.0-uv.y));

    vec2 uv3 = uv;
    uv3 -= 0.5;
    uv3 = rotate2d(-(iTime/8.0)*PI)*uv3;
    uv3 += 0.5;



    t_color += (1.0 - drawCircle(uv3, 0.253+0.001*abs(sin(iTime))+sin(iTime)*0.05))*drawCircle(uv3, 0.251-0.001*abs(sin(iTime))+sin(iTime)*0.05);
    t_color *= 1.0-vec3(step (0.4755, uv3.x)*step (0.4755, 1.0-uv3.x));
    t_color *= 1.0-vec3(step (0.4755, uv3.y)*step (0.4755, 1.0-uv3.y));
    color += t_color;

    color += vec3(1.0 - drawCircle(uv, 0.0809))*vec3(drawCircle(uv, 0.0801))*mat3(0.5+0.5*abs(sin(iTime)),0.0,0.0,
                                                                                  0.0, 0.5+0.5*abs(cos(iTime)), 0.0,
                                                                                  0.0,0.0,0.0);

    color.g += (drawSquare(
                    uv, 0.45-0.015*abs(cos(iTime)), vec2(0.235+sin(iTime)*0.01,0.234+sin(iTime)*0.02))
              *(1.0-drawSquare(
                    uv, 0.453-0.015*abs(cos(iTime)), vec2(0.235+sin(iTime)*0.01,0.234+sin(iTime)*0.02))));

    color.r += (drawSquare
                    (uv, 0.45-0.015*abs(sin(iTime)), vec2(-0.267+cos(iTime)*0.01,-0.178+sin(iTime)*0.02))*
                    (1.0-drawSquare(uv, 0.453-0.015*abs(sin(iTime)), vec2(-0.267+cos(iTime)*0.01,-0.178+sin(iTime)*0.02))));

    vec2 uv2 = uv;
    uv2 -= vec2(0.5); 
    uv2 = rotate2d( (sin(mod(iTime/2.0, PI*2.50))*TWO_PI) ) * uv2;
    uv2 += vec2(0.5);

  color += 1.0-drawNshape(3, uv, 0.015, vec2(1.0, 0.28+0.035*abs(sin(iTime))), PI);
  color += 1.0-drawNshape(3, uv, 0.015, vec2(1.0, 1.72-0.035*abs(sin(iTime))), 2.0*PI);
  color += 1.0-drawNshape(3, uv, 0.015, vec2(0.28+0.035*abs(sin(iTime)), 1.0), 0.5*PI);
  color += 1.0-drawNshape(3, uv, 0.015, vec2(1.72-0.035*abs(sin(iTime)),1.0), 1.5*PI);
  color.g += 1.0-drawNshape(3, uv2, 0.015+0.015*abs(sin(mod(iTime/2.0, PI*2.50))), vec2(1.75-0.035*abs(sin(iTime)),1.0), 1.5*PI);

  color.rg += vec2(1.0,0.5)*(1.0-drawNshape(5, uv, 0.115+0.0042*sin(iTime), vec2(1.0), 1.5*PI+sin(iTime)))*drawNshape(5, uv, 0.125+0.0042*cos(iTime), 
                          vec2(1.0), 1.0*PI+sin(iTime));

  color.g += (step(0.4995, uv.x)*step(uv.x, 0.5005))+(step(0.4995, uv.y)*step(uv.y, 0.5005));
  color.r += ((step(0.4995, uv.x)*step(uv.x, 0.5005))+(step(0.4995, uv.y)*step(uv.y, 0.5005))) * abs(sin(iTime));




    // Output to screen
    fragColor = 0.5*tex0+vec4(color,1.0);
}
