const float PI = 3.14,
    width = 0.8,
    height = 0.6,
    R = 0.07;
const vec2 center = vec2(0.5, -0.2);

vec2 rotZ(vec2 point, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    vec2 finalPoint = mat2(c, -s, s, c) * point;
    return finalPoint;
}

vec4 drawRect(float width, float height, vec2 U, float d) {
    vec4 o = vec4(1.);
    vec2 rotU = rotZ(U, -PI/4. * 0.9);
    o *= vec4(min(exp((-abs(rotU.y - rotU.x) + height)/d), 1.));
    rotU = rotZ(U, PI/4.);
    o *= vec4(min(exp((-abs(rotU.y - rotU.x) + width)/d), 1.));
    return o;
}

vec4 drawBall(float radius, vec2 U, vec2 center, float d) {
    return vec4(min(exp((-distance(U, center) + radius)/d), 1.));
}

void mainImage( out vec4 O, in vec2 u ) {
    vec2 Res = iResolution.xy, 
        U = (2. * u - Res) / Res.y - center;
    O = vec4(1.);
    vec2 mDir = normalize((2. * iMouse.xy - Res) / Res.y);
    O *= drawRect(width, height, U, abs(sin(iTime)) * 0.2 + 0.1)
      - drawRect(width * 0.85, height  * 0.85, U, 0.01)
      + drawBall(0.08, U, vec2(-0.25, 0.05) + R * mDir, abs(sin(iTime)) * 0.03 + 0.01)
      + drawBall(0.12, U, vec2(0.25, 0.05) + R * mDir, abs(sin(iTime)) * 0.03 + 0.01);
}
