float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
    return mix(a, b, h) - k*h*(1.0-h);
}

float metaball(vec2 p, vec2 c) {
    return clamp(0.025/pow(length(p-c),1.5),0.,1.);
}

float color(vec2 p) {
    vec2 sphere_0_pos = vec2(0.5 + sin(iTime)/4., 0.5);
    vec2 sphere_1_pos = vec2(0.9 + cos(iTime)/4., 0.5 + sin(iTime)/4.);
    vec2 sphere_2_pos = vec2(0.9 + sin(iTime+1.)/4., 0.5 + cos(iTime)/4.);
    float sphere_0 = metaball(p, sphere_0_pos);
    float sphere_1 = metaball(p, sphere_1_pos);
    float sphere_2 = metaball(p, sphere_2_pos);

    return sphere_0 + sphere_1 + sphere_2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= 16./9.;

    fragColor = vec4(vec3(color(uv)>1.),1.0);
}
