// --- noise functions from https://www.shadertoy.com/view/XslGRr  Created by inigo quilez - iq/2013

mat3 m = mat3( 0.00,  0.80,  0.60,
              -0.80,  0.36, -0.48,
              -0.60, -0.48,  0.64 );

float hash( float n )
{
    return fract(sin(n)*43758.5453);
}

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0 + 113.0*p.z;

    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                        mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
                    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                        mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
    return res;
}

float fbm( vec3 p )
{
    float f;
    f  = 0.5000*noise( p ); p = m*p*2.02;
    f += 0.2500*noise( p ); p = m*p*2.03;
    f += 0.1250*noise( p ); p = m*p*2.01;
    f += 0.0625*noise( p ); 
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
	vec3 v;
	vec3 p = 4.*vec3(uv,0.)+iTime*(.9,.7,1.2);
	float x = fbm(p);
	v = (.5+.5*sin(x*vec3(30.,20.,10.)));
	vec3 T = texture(iChannel0,.02*v.xy+fragCoord.xy / iResolution.xy).rgb*1.4-.2;
	vec3 T1,T2;
	T1 = vec3(0.6,0.6,.7); T1 *= .5*(T+1.);
	T2 = vec3(1.,1.,1.); 
	v = mix(T1, T2,T);
	fragColor = vec4(v,1.0);

}
