	#define SHARPEN_INTENSITY	float(1.0) // 0.0 - 1.0
	#define SHARPEN_VALUE_0 	float(2.0)
	#define SHARPEN_VALUE_1 	float(-0.125)
	#define SHARPEN_WIDTH 		float(1.6)
	#define float4 		vec4   
	#define float3 		vec3
	#define float2 		vec2

	float3	sharpen_image(float2 tc0, float3 base_color)
	{
		float	dx 					= SHARPEN_WIDTH / iResolution.x;
		float	dy 					= SHARPEN_WIDTH / iResolution.y;

		float4	c1 					= texture( iChannel0,  tc0 + float2(-dx, -dy)) * SHARPEN_VALUE_1;
		float4	c2 					= texture( iChannel0,  tc0 + float2(  0, -dy)) * SHARPEN_VALUE_1;
		float4	c3 					= texture( iChannel0, tc0 + float2(-dx,   0)) * SHARPEN_VALUE_1;
		float4	c4 					= texture( iChannel0,  tc0 + float2( dx,   0)) * SHARPEN_VALUE_1;
		float4	c5 					= texture( iChannel0, tc0 + float2(  0,  dy)) * SHARPEN_VALUE_1;
		float4	c6 					= texture( iChannel0,  tc0 + float2( dx,  dy)) * SHARPEN_VALUE_1;
		float4	c7 					= texture( iChannel0, tc0 + float2(-dx, +dy)) * SHARPEN_VALUE_1;
		float4	c8 					= texture( iChannel0, tc0 + float2(+dx, -dy)) * SHARPEN_VALUE_1;
		float4	c9 					= texture( iChannel0,  tc0) * SHARPEN_VALUE_0;
		float4	c0 					= (c1 + c2 + c3 + c4 + c5 + c6 + c7 + c8 + c9);

		return	mix(base_color, c0.xyz, SHARPEN_INTENSITY);	
	}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord / iResolution.x;

    vec3 colb = texture( iChannel0, uv ).xyz;
    if (uv.x > 0.5)
        colb = sharpen_image(uv, colb);

	fragColor = vec4( colb, 1.0 );
}
