float seg( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp(dot(pa,ba)/dot(ba,ba),0.0,1.0);
    return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    float hr = iResolution.x / iResolution.y;

    uv.x *= hr;

    float r = 0.025;
    float t = 0.002 * length(uv);
    float d = pow(uv.x,1.) * pow(uv.y,1.);
    d += pow(.00001*abs(cos(uv.x * uv.y * hr)),1.*abs(sin(iTime*.5)));

    vec3 col = vec3(.85);
    col = mix(col, vec3(.0,.8,.9), 1.0-smoothstep(.0,t,/*sin*/(length(uv)-length(m)*.5)));
    col = mix(col, vec3(.9,.0,.0), 1.0-smoothstep(.0,0.002,seg(uv, vec2(0.0), m*.5)));
    //col = mix(col, vec3(.0,.7,.0), 1.0-smoothstep(.0+abs(sin(iTime*2.))*.0015,t*abs(cos(iTime*1.)),abs(d-r)));
    col = mix(col, vec3(.5,.5,.5), 1.0-smoothstep(.0,t,abs(uv.x*uv.y)));
    col = mix(col, vec3(.9,.0,.0), 1.0-smoothstep(0.,t,length(uv)-.01));
    col = mix(col, vec3(.0,.7,.0), 1.0-smoothstep(.0,t,abs(d-r)));

    fragColor = vec4(col,1.0);
}
