void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Settings 
    float dotRes = 60.;
    float size = 6.;
    //Use to cancel certain colours
    float r = 1.;
    float g = 5.;
    float b = 1.;
    float antialiasing = 0.05;
    float invert = 1.;
    vec3 backColor = vec3(0.3,0.1,0.2);
    vec3 circleColor = vec3(0.9,0.25,0.3);

    //Setup
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pattern = uv;
    float y = iResolution.y/dotRes;
    vec2 pixelSize = y /iResolution.xy;
    vec2 coord = pixelSize * floor(uv/pixelSize);
    pattern.x *= iResolution.x/iResolution.y;

    //Shader
    vec4 text = texture(iChannel0, coord);
    float density = text.x*r+text.y*g+text.z*b;
    pattern*= dotRes;
    pattern = fract(pattern);
    pattern -= .5;
    float c = smoothstep(density/size-antialiasing,density/size,length(pattern));
    if(invert==1.){
        c = (c-1.)*-1.;
    }
    circleColor *= c;
    backColor *= 1.-c;
    fragColor = vec4(circleColor+backColor,1.);
}
