float sphereSDF(vec2 pos, float r, vec2 center)
{
    // center
    return pow(max(r - length(pos - center), 0.), r*.3)*2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 중앙을 (.5,.5)으로 잡는다. 비율은 y기준으로 유지.
	vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 val = vec3(0,0,0);

    val += vec3(sphereSDF(uv, .05*abs(sin(200.+iTime*6.))+.25, vec2(0.,0.)) ,0.,0.);
    val += vec3(0.,0.,sphereSDF(uv, .05*abs(sin(0.+iTime*6.))+.25, vec2(.4,.0)));
    val += vec3(0.,sphereSDF(uv, .05*abs(sin(400.+iTime*6.))+.25, vec2(-.4,.0)), 0.);

    fragColor = vec4(vec3(val), 1.);
}
