//:) by notargs 

#define PI 3.1415926535

float rand(float a, float b)
{
    return fract(sin(dot(vec2(a, b) ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	//vec2 pos = ( fragCoord.xy - iResolution.xy / 2.) / iResolution.y + iMouse.xy - 0.5;
    vec2 pos = ( fragCoord.xy - iResolution.xy / 2.) / iResolution.y- 0.5;
    pos-=vec2(-0.5,-0.5);

	vec3 color = vec3(0, 0, 0);

	for(int i = 0; i < 200; i++)
	{
		float fi = float(i);
		float t  = pow(fract(iTime * 2.), 0.5);
		float t2 =     floor(iTime * 2.);
		float a = float(i) / PI;
		float len = 0.3 + (1. - t) * 0.2 * rand(fi, t2);
		vec2 p = pos + vec2(cos(a), sin(a)) * len;
		float intencity = pow(0.003 / length(p), 1.5);
		color += intencity * vec3(rand(fi, 1.), rand(fi, 2.), rand(fi, 3.));
	}
	for(int i = 0; i < 20; i++)
	{
		float fi = float(i);
		float t  = pow(fract(iTime * 2.), 0.1);
		float t2 =     floor(iTime * 2.);
		float a = float(i - 10) / PI / 5.;
		float len = 0.3 + (1. - t) * 0.2 * rand(fi, t2);
		vec2 p = pos + vec2(sin(a), cos(a)) * len;
		p += vec2(0, -0.1);
		float intencity = pow(0.003 / length(p), 1.5);
		color += intencity * vec3(rand(fi, 1.), rand(fi, 2.), rand(fi, 3.));
	}
	for(int i = 0; i < 20; i++)
	{
		float fi = float(i);
		float t  = pow(fract(iTime * 2.), 0.1);
		float t2 =     floor(iTime * 2.);
		float a = float(i - 10) / PI;
		float len = 0.04 + (1. - t) * 0.2 * rand(fi, t2);
		vec2 p = pos + vec2(sin(a), cos(a)) * len;
		p += vec2(-0.13, -0.05);
		float intencity = pow(0.003 / length(p), 1.5);
		color += intencity * vec3(rand(fi, 1.), rand(fi, 2.), rand(fi, 3.));
	}
	for(int i = 0; i < 20; i++)
	{
		float fi = float(i);
		float t  = pow(fract(iTime * 2.), 0.1);
		float t2 =     floor(iTime * 2.);
		float a = float(i - 10) / PI;
		float len = 0.04 + (1. - t) * 0.2 * rand(fi, t2);
		vec2 p = pos + vec2(sin(a), cos(a)) * len;
		p += vec2(0.13, -0.05);
		float intencity = pow(0.003 / length(p), 1.5);
		color += intencity * vec3(rand(fi, 1.), rand(fi, 2.), rand(fi, 3.));
	}
	color += vec3(fract(pos.y * 20. + iTime) < 0.5) * 0.05;
	color += vec3(fract(pos.y * 20. - iTime) < 0.5) * 0.05;
	fragColor = vec4(color, 1.0);

}
