float Q(float x, float y){
           return max(abs(x), abs(y)) - 1.0 ;   }

    float R(float x, float y){
           return abs(x) + abs(y) - 1.0 ;   }

    float C(float x, float y){
           return x*x + y*y - 1.0 ;       }

    float F(float x, float y){
            // return sin(x) - 2.0*y; 
             return x*x + y*y - 6.0 ; 
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 p = 1.0 - 2.0* fragCoord/iResolution.xy;
    vec2 m = 1.0 - 2.0* iMouse.xy/iResolution.xy;

     float x = p.x * iResolution.x/iResolution.y;
     float y = p.y ;

     float mx = m.x * iResolution.x/iResolution.y;
     float my = m.y ;

            x/=mx;
            y/=my; 

      float  z;
             //z = F(x,y);
             // z = min(z, C(x,y));
              z = min(z, R(x-1.5,y+0.5));
              z = min(z, Q(x+2.5,y+0.75));

       float   f = 1.25/z; // 0.5*z; 


    // Time varying pixel color
               vec3 col;
                if(f <= 0.0) col.r = -f;
                   else      col.b =  f;


    // Output to screen
                fragColor = vec4(col,1.0);
}
