float Circle(vec2 uv, vec2 position, float r, float blur) 
    {
        float d = length(uv - position);
        float c = smoothstep(r, r-blur, d);
        return c;
    }

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = fragCoord.xy / iResolution.xy;  
    uv -= 0.5;  
    uv.x *= (iResolution.x / iResolution.y); 
    // голова
    float c = Circle(uv, vec2(.0, -.1), .2, .005);
    // глаза
    c -= Circle(uv, vec2(.06, -.08), .025, .001); 
    c -= Circle(uv, vec2(-.06, -.08), .025, .001); 
    c -= Circle(uv, vec2(.06, -.1), .05, .001);    
    c -= Circle(uv, vec2(-.06, -.1), .05, .001); 
    c -= Circle(uv, vec2(.06, -.08), .05, .001);    
    c -= Circle(uv, vec2(-.06, -.08), .05, .001); 
    c -= Circle(uv, vec2(.06, -.06), .05, .001);    
    c -= Circle(uv, vec2(-.06, -.06), .05, .001);
    c += Circle(uv, vec2(.06, -.1), .04, .001);    
    c += Circle(uv, vec2(-.06, -.1), .04, .001); 
    c += Circle(uv, vec2(.06, -.08), .04, .001);    
    c += Circle(uv, vec2(-.06, -.08), .04, .001); 
    c += Circle(uv, vec2(.06, -.06), .04, .001);    
    c += Circle(uv, vec2(-.06, -.06), .04, .001);
    //нос
    c -= Circle(uv, vec2(.0, -.17), .025, .001);
    //рот 
    c += Circle(uv, vec2(.0, -.23), .025, .001);
    c -= Circle(uv, vec2(.0, -.22), .025, .001);
    // уши
    c += Circle(uv, vec2(.04, .12), .05, .005);
    c += Circle(uv, vec2(-.03, .12), .05, .005);  
    c += Circle(uv, vec2(.052, .143), .057, .005);
    c += Circle(uv, vec2(-.055, .158), .056, .005); 
    c += Circle(uv, vec2(.07, .17), .061, .005);
    c += Circle(uv, vec2(-.07, .177), .061, .005); 
    c += Circle(uv, vec2(.082, .19), .07, .005);
    c += Circle(uv, vec2(-.09, .2), .07, .005); 
    c += Circle(uv, vec2(.1, .23), .08, .005);
    c += Circle(uv, vec2(-.13, .23), .09, .005); 
    //руки
    c += Circle(uv, vec2(.22, -.14), .05, .005);
    c += Circle(uv, vec2(-.22, -.14), .05, .005);  
    //ноги
    c += Circle(uv, vec2(.14, -.27), .05, .005);
    c += Circle(uv, vec2(-.14, -.27), .05, .005);  


    fragColor = vec4(vec3(c), 1.0);
}
