    float Crcl(vec2 xy, float r, vec2 pos)
    {
        float d = length(xy-pos);
        float cl = smoothstep(r, r-0.008, d);
        return cl;
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy/iResolution.xy;
    xy -= 0.5;
    xy.x *= iResolution.x/iResolution.y;

    float cheb = Crcl(xy, 0.2, vec2(0.0, -0.1));
        cheb += Crcl(xy, 0.15, vec2(0.0, 0.2));
        cheb += Crcl(xy, 0.125, vec2(0.2, 0.2));
        cheb += Crcl(xy, 0.125, vec2(-0.2, 0.2));
        cheb += Crcl(xy, 0.075, vec2(-0.2, -0.25));
        cheb += Crcl(xy, 0.075, vec2(0.2, -0.25));
        cheb += Crcl(xy, 0.07, vec2(-0.22, -0.02));
        cheb += Crcl(xy, 0.07, vec2(0.22, -0.02));
        cheb -= Crcl(xy, 0.03, vec2(-0.05, 0.2));
        cheb -= Crcl(xy, 0.03, vec2(0.05, 0.2));
        cheb -= Crcl(xy, 0.04, vec2(0.0, 0.15));
        cheb += Crcl(xy, 0.04, vec2(0.0, 0.16));


    vec3 pct = vec3(1.0, 1.0, 1.0) * cheb;
    fragColor = vec4(pct, 0.0);
}
