#define c1 vec3(1.,0.5,1.5)

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
    //return abs(fract((a+c1.xy)*scale)-.5)-ceil(a.y/2.)/4.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;  
    float t1 = 36.*4.*4.*1.5;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
        uv = uv.xy + iMouse.xy / iResolution.xy/t1;

    float scale = c1.z;
    float offset = 0.;
    float offset1 = iTime/1000.;
    float p1 = 1.;
    for(int i=0;i<6;i++)
    {
        vec2 t2 = vec2(0.);
        vec2 t3 = vec2(0.);
        for(int k = 0; k < 3; k++){
            uv += (t2.yx);
            uv /= scale;
            //uv += ceil(t2*2.)/2.; //lace pattern

            uv += vec2(1.);
            //if(uv.y>uv.x) uv = -uv.yx; else uv *= -sign(uv-uv.yx);
            t2 = p1*triangle_wave(uv.yx-.5,1.5);
            t3 = -p1*triangle_wave(uv,1.5);
            uv.yx = (t2+t3);
            p1 *= -1.;
        }

        //col.x = abs(col.x-.5);
        col.x = 1.-min(uv.y+uv.x+col.x,col.x)*2.;
        //col.x = min(uv.y-uv.x+col.x,col.x*2.);

        //uv.x *= -1.;
        col = abs(col.yzx-vec3(1.5-col.x))/2.;
        //col = abs(col.yzx+vec3(col.x)*2.)/3.;

        uv.y *= -1.;
    }
    fragColor = vec4(col*2.,1.0);
}
