void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
}

#define PIX 2.

bool init(float x) {
    int i = int(x);
    ivec2 size = textureSize(iChannel1, 0);
    return texelFetch(iChannel1, ivec2(i % size.x, i / size.x), 0).r < .5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = floor(fragCoord / PIX) + .5, res = floor(iResolution.xy / PIX);
    bool o;
    if (fragCoord.y < 20.) {
        o = texture(iChannel0, (p+vec2( 0,-1))/res).r > 0.;
        if (iFrame < 10) {
            o = init(p.x);
        }
        if (iMouse.w > 0. && iMouse.y < 20. && floor(p.x) == floor(iMouse.x/PIX)) {
            o = !o;
        }
    } else {
        bool a = texture(iChannel0, (p+vec2(-1,-1))/res).r > 0.;
        bool b = texture(iChannel0, (p+vec2( 0,-1))/res).r > 0.;
        bool c = texture(iChannel0, (p+vec2( 1,-1))/res).r > 0.;
        o = a ^^ (b || c);
    }
    fragColor = vec4(vec3(o), 1);
}
