    float Circle(float x, float y, float r){
        return x*x+y*y-r;
    }
    float Ellips(float x, float y ,float a,float b, float r){
        return (x/a*x/a)+(y/b*y/b)-r;
    }
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 p=1.0-2.0*fragCoord.xy/iResolution.xy;
     float x=p.x * iResolution.x/iResolution.y;
     float y=p.y; 

    float c1=Ellips(x - 1.65,y+.5, .06, .45, .7);
    float c2=Ellips(x - 1.55,y+.15, .1, .02, 1.5); 
    float c3=Ellips(x - 1.55,y+.86, .1, .02, 1.5); 

    float k1=Ellips(x - 1.32,y+.4, .06, .3, .7); 
    float k2=Circle(x - 1.23,y+.4, .005);
    float k3=Circle(x - 1.15,y+.52, .005);
    float k4=Circle(x - 1.15,y+.27, .005);

    float y1=Ellips(x - .75,y+.4, .06, .3, .7);     
    float y2=Ellips(x - .85,y+.15, .1, .02, 1.5); 
    float y3=Ellips(x - .85,y+.35, .1, .02, 1.5); 
    float y4=Ellips(x - .95,y+.5, .15, 1., .02); 

    float p1=Ellips(x - .58,y+.4, .06, .3, .7); 
    float p2=Ellips(x - .5,y+.35, .1, .02, 1.5);     
    float p3=Ellips(x - .5,y+.62, .1, .02, 1.5);
    float p4=Ellips(x - .37,y+.5, .15, 1., .02);

    float i1=Ellips(x - .22,y+.35, .06, .25, .7); 
    float i2=Circle(x - .22,y+.65, .005);    

    float x1=Circle(x + .06,y+.4, .005);
    float x2=Circle(x - .02,y+.52, .005);
    float x3=Circle(x - .02,y+.27, .005);
    float x4=Circle(x + .16,y+.52, .005);
    float x5=Circle(x + .16,y+.27, .005);  

    float i_1=Ellips(x + .36,y+.35, .06, .25, .7); 
    float i_2=Circle(x + .36,y+.65, .005);    

    float n1=Ellips(x + .54,y+.4, .06, .3, .7); 
    float n2=Ellips(x + .8,y+.4, .06, .3, .7); 
    float n3=Ellips(x + .66,y+.39, .1, .02, 1.5);  

    float I1=Ellips(x - .4,y-.4, .06, .45, .7);
    float I2=Circle(x - .58,y-.22, .005);
    float I3=Circle(x - .5,y-.1, .005);
    float I4=Ellips(x - .5,y-.75, .1, .02, 1.5);  
    float I5=Circle(x - .57,y-.68, .003);

    float v1=Ellips(x - .2,y-.52, .06, .3, .7); 
    float v2=Ellips(x - .1,y-.52, .1, .02, 1.5);     
    float v3=Ellips(x - .1,y-.28, .1, .02, 1.5);
    float v4=Ellips(x +.03,y-.4, .15, 1., .02);
    float v5=Ellips(x +.03,y-.6, .15, 1., .02);
    float v6=Ellips(x - .1,y-.75, .1, .02, 1.5);  

    float a1=Circle(x + .17,y-.69, .005);
    float a2=Circle(x + .23,y-.57, .005);
    float a3=Circle(x + .27,y-.45, .005);
    float a4=Circle(x + .33,y-.33, .005);
    float a5=Circle(x + .53,y-.69, .005);
    float a6=Circle(x + .48,y-.57, .005);
    float a7=Circle(x + .42,y-.45, .005);
    float a8=Ellips(x + .35,y-.6, .1, .02, 1.5); 

    float n_1=Ellips(x + .7,y-.52, .06, .3, .7); 
    float n_2=Ellips(x + .96,y-.52, .06, .3, .7); 
    float n_3=Ellips(x + .84,y-.52, .1, .02, 1.5); 

    float z=c1;
     z = min(z, c2);
     z = min(z, c3);

     z = min(z, k1);
     z = min(z, k2);    
     z = min(z, k3); 
     z = min(z, k4);

     z = min(z, y1);    
     z = min(z, y2);    
     z = min(z, y3);    
     z = min(z, y4);  

     z = min(z, p1);
     z = min(z, p2);
     z = min(z, p3);
     z = min(z, p4);

     z = min(z, i1);
     z = min(z, i2);

     z = min(z, x1);    
     z = min(z, x2); 
     z = min(z, x3);   
     z = min(z, x4); 
     z = min(z, x5);

     z = min(z, i_1);
     z = min(z, i_2);

     z = min(z, n1);
     z = min(z, n2);
     z = min(z, n3);

     z = min(z, I1);
     z = min(z, I2);
     z = min(z, I3);
     z = min(z, I4);
     z = min(z, I5);

     z = min(z, v1);  
     z = min(z, v2);
     z = min(z, v3);
     z = min(z, v4);
     z = min(z, v5);
     z = min(z, v6);    

     z = min(z, a1);
     z = min(z, a2);
     z = min(z, a3);
     z = min(z, a4);
     z = min(z, a5);
     z = min(z, a6);
     z = min(z, a7);
     z = min(z, a8);

     z = min(z, n_1);
     z = min(z, n_2);
     z = min(z, n_3);

    vec3 col=vec3(1.,1.,0.);
     if(z<0.) col.z=z; 
     else col.z = 1.;
     fragColor = vec4(col, 1.0 );   
}
