void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float time = iTime+uv.x*0.1;
    vec3 s = soundfunc(1,time);
    vec3 col = vec3(0.);
    //col += t*uv.y*20.;
    //col 
    col.rg += 1.*step(s.z/1000.+uv.y,1.)*0.8;
    //col.rgb += 1.*step(s.z/100.+uv.y,1.)*0.8;
    col.bg += step(s.x+uv.y*2.,1.)*0.3;
    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 mainSound( int samp, float time )
{
    return soundfreq(samp,time);
}

float a = pow(2.,1./12.);
#define pi 3.14159265
#define tau pi*2.

//https://www.shadertoy.com/view/ttBXRG
float staircase( in float x, in float k )
{
    float i = floor(x);
    float f = fract(x);

    float a = 0.5*pow(2.0*((f<0.5)?f:1.0-f), k);
    f = (f<0.5)?a:1.0-a;

    return i+f;
}


float scale(float t) {
    t = 440.*pow(a,(staircase(t*2.,0.001)/2.)-5.);
    //t = 440.*pow(a,floor(abs(fract(t)-0.5)*24.)*2.-12.);
    return t;
}
float tone(float t,float tm) {
    t = sin(6.2831*t*tm);
    return t;
}
float wop(float t,float tm,float bps) {
    t *= pow(exp(-4.*abs(fract(-tm*bps-0.15)-0.5)),13.);
    return t;
}
float wop1(float t,float tm,float bps) {
    t *= pow(exp(-4.*abs(fract(-tm*bps-0.15)-0.5)),5.);
    return t;
}
float bong(float t,float tm,float bps) {
    t*=exp(-4.0*fract(tm*bps));
    //t *= pow(exp(-4.*abs(fract(-tm*bps-0.15)-0.5)),13.);
    //t*=exp(-24.0*fract(-tm*bps+0.7));
    //t *= step(fract(tm*bps+0.03),0.03);
    //float tc = clamp(sin(tm*tau*bps+4.66)*0.5+0.5,-1.,0.1);
    //t *= 1.-pow(1.-tc,2.);
    //t *= 22.;
    //t *= 0.;
    return t;
}

float shep(int ste,float time) {
    float f = 40.;
    float s = 0.;
    float bps = 0.5;
    for (int i=0;i<ste;i++) {
        //bps *= 2.;
        s += tone(time,f);
        s += tone(time,f+bps);
        bps *= 1.05+sin(time);
        f *= 1.+fract(time*0.01+float(i*90)/13.);
        //n += time*0.1;
        //n = fract(n*12.)/12.;
        //s += bong(tone(time,scale(n)),time,bps)*am;
        //am *= 0.88;
        //s += bong(tone(time,scale(t)),time,float(i));
    }
    return s;
}

vec3 soundfunc( int samp, float time )
{
    float t = time*0.1;
    float ot = t*0.02;
    vec2 fs = vec2(0.);
    float s = 0.;
    float stime = time+1.;
    s = wop1(tone(scale(fract(stime/2./20.)*4.),time),time,0.1)*0.5;
    float bps = 0.5;
    float am = 1.;
    for (int i=0;i<6;i++) {
        s += bong(tone(scale(fract(stime/12./2.+float(i))*4.*bps)-24.,time),time,bps)*am;
        am *= 0.7;
        bps *= 2.;
    }
    bps = 2.;
    am = 0.7;
    for (int i=0;i<6;i++) {
        s += wop(tone(scale(fract(stime/8./2.+float(i))*4.*bps)-2.,time),time,bps)*am;
        am *= 0.7;
        bps *= 2.;
    }
    fs = vec2(s)*0.5;
    return vec3( fs.x , fs.y , ot );
}
vec2 soundfreq( int samp, float time )
{
    return soundfunc(samp,time).xy;
}
