void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 center = vec2(sin(iTime * 0.125), cos(iTime * 0.125)) * 0.5;
    vec2 uvc = uv * 2.0 - 1.0 - center;
    float r = sqrt(dot(uvc, uvc) / 2.0);
    float theta = atan(uvc.y, uvc.x);

    float scaled_r = r * (1.0 + pow(sin(iTime * 0.133333), 2.0) * 4.0);
    float twist_theta = theta + r * (1.0 + pow(sin(iTime * 0.571428), 2.0) * 2.0);

    float ripple = pow(sin(twist_theta * 4.0), 7.0);
    float ripple2 = pow(sin(twist_theta * 4.0 + 3.141592654 * 0.125), 7.0);
    float ripple3 = pow(sin(twist_theta * 4.0 + 3.141592654 * 0.375), 7.0);

    fragColor = vec4(r * ripple, r * ripple2, r * ripple3, 1.0);
}
